
module.exports.concat = function(chunks, length) {
  var buff = new Buffer(length);
  var offset = 0;
  var chunk;

  while (chunk = chunks.shift()) {
    var len = chunk.length;
    chunk.copy(buff, offset, 0, len);
    offset += len;
  };

  return buff;
};

module.exports.isBuffer = Buffer.isBuffer || function(buff) {
  return buff instanceof Buffer;
};
